DROP TABLE IF EXISTS [CountriesCoordinates];
CREATE TABLE [CountriesCoordinates]
(
  Country     VARCHAR(100),
  Alpha2      VARCHAR(100),
  Alpha3      VARCHAR(100),
  NumericCode INT,
  Latitude    FLOAT,
  Longitude   FLOAT
);

